        $ctrl->setTargetScript('ilias.php');
        $ctrl->initBaseClass($base_class);

        foreach ($params as $key => $value) {
            $ctrl->setParameterByClass($target_class, $key, $value);
        }

        $ctrl->redirectByClass($classes, $cmd);
    }

