                        if (!ilObjLearningPathAccess::hasWriteAccess()) {
                            ilObjLearningPathAccess::redirectNonAccess($this);
                        }

                        $this->{$cmd}();
                        break;

                    default:
                        // Unknown command
                        ilObjLearningPathAccess::redirectNonAccess(ilRepositoryGUI::class);
                        break;
                }
                break;
        }
    }


